const TabContent = (props:ITabContentProps)=>{
  let tabjsx =<div/>;
  switch(props.activeTabName){
    case TabEnum.Templates:
      tabjsx = renderTemplates();
      break;
    case TabEnum.Events:
      tabjsx = renderEvents();
      break;      
    case TabEnum.Style:
      tabjsx = renderStyle();
      break;
  }
  if (props.activeTabName == TabEnum.Templates){
    tabjsx = renderTemplates();
  }
  return(
    <div className="tab-content" >
      {tabjsx}
    </div>
  )
}


